import React, {Fragment,useState} from "react";
import "./InputDoctorStyling.css";


const InputDoctor = () => {


    const[f_name, setf_name] = useState("");
    const[l_name, setl_name] = useState("");
    const[phone, setphone] = useState("");
    const[adress, setadress] = useState("");
    const[email, setemail] = useState("");
    const[dob, setdob] = useState("");
    const[speciality, setspeciality] = useState("");

    const onSubmitForm = async(e) => {
        e.preventDefault();
        try {
            const body = {  f_name, l_name, phone, adress, email, dob, speciality };
            const response = await fetch("http://localhost:3001/inputdoctor",{
                method: "POST",
                headers: {"Content-Type": "application/json"},
                body: JSON.stringify(body)
            });
            console.log(response);

        } catch (err) {
            console.error(err.message);  
        }
    }

    return (
         <Fragment>
                <div>
                <h1><u><p class="text-center">Add New Doctor</p></u></h1>

                <form className="d-flex mt-5 " onSubmit={onSubmitForm}>
                <div className="form-group">
                <div >
                    <h5>Enter first name:</h5>
                    <input type="text" className="form-control" value={f_name}
                     onChange={e =>setf_name(e.target.value)}/>

                    <h5>Enter last name:</h5>
                    <input type="text" className="form-control" value={l_name}
                     onChange={e =>setl_name(e.target.value)}/>

                    <h5>Enter phone:</h5>
                    <input type="text" className="form-control" value={phone}
                     onChange={e =>setphone(e.target.value)}/>

                    <h5>Enter adress:</h5>
                    <input type="text" className="form-control" value={adress}
                     onChange={e =>setadress(e.target.value)}/>

                    <h5>Enter email:</h5>
                    <input type="text" className="form-control" value={email}
                     onChange={e =>setemail(e.target.value)}/>


                    <h5>Enter date of birth:</h5>
                    <input type="text" className="form-control" value={dob}
                     onChange={e =>setdob(e.target.value)}/>

                    <h5>Enter speciality:</h5>
                    <input type="text" className="form-control" value={speciality}
                     onChange={e =>setspeciality(e.target.value)}/>
                     <h5>           </h5>

                    </div>
                    <p><button className="btn btn-success btn-sm">Add</button></p>
                    </div>
                </form>
                </div>
            </Fragment>

    );
};



export default InputDoctor;
